// Based on Responsive & Accessible Data Table by Charlie Chathcart: http://codepen.io/pixelchar/pen/rfuqK

.responsive-table {
  width: 100%;
  margin-bottom: 1.5em;


  @media (min-width: @bp-md) {
    font-size: .9em; 
  }

  @media (min-width: @bp-lg) {
    font-size: 1em; 
  }

  thead {
    // Accessibly hide <thead> on narrow viewports
    position: absolute;
    clip: rect(1px 1px 1px 1px); /* IE6, IE7 */
    clip: rect(1px, 1px, 1px, 1px);
    padding: 0;
    border: 0;
    height: 1px; 
    width: 1px; 
    overflow: hidden;

    @media (min-width: @bp-md) {
      // Unhide <thead> on wide viewports
      position: relative;
      clip: auto;
      height: auto;
      width: auto;
      overflow: auto;
    }

    th {
      background-color: @brand-color-03;
      font-weight: normal;
      text-align: left;
      color: white;

      &:first-of-type {
        text-align: left; 
      }
    }
  }


  // Set these items to display: block for narrow viewports
  tbody,
  tr,
  th,
  td {
    display: block;
    padding: 0;
    text-align: left;
    white-space: normal;
  }

  tr {   
    @media (min-width: @bp-md) {
      // Undo display: block 
      display: table-row; 
    }
  }

  th,
  td {
    padding: .5em;
    vertical-align: middle;

    @media (max-width: @bp-sm) {

      font-size:.8em;
    }

    @media (min-width: @bp-md) {
      // Undo display: block 
      display: table-cell;
      padding: .5em;
    }

    @media (min-width: @bp-lg) {
      padding: .75em .5em; 
    }

    @media (min-width: @bp-xlg) {
      padding: .75em; 
    }
  }

  caption {
    margin-bottom: 1em;
    font-size: 1em !important;


    @media (min-width: @bp-md) {
      font-size: 1.5em;
    }
  }

  tfoot {
    font-size: .8em;
    font-style: italic;

    @media (min-width: @bp-lg) {
      font-size: .9em;
    }
  }

  tbody {
    @media (min-width: @bp-md) {
      // Undo display: block 
      display: table-row-group; 
    }

    tr {
      margin-bottom: 1em;

      @media (min-width: @bp-md) {
        // Undo display: block 
        display: table-row;
      }

      &:last-of-type {
        margin-bottom: 0; 
      }

      &:nth-of-type(even) {
        @media (min-width: @bp-md) {
          background-color: rgba(94,93,82,.1);
        }
      }
    }

    th[scope="row"] {
       background-color: @brand-color-03;
      color: white;

      @media (min-width: @bp-md) {
        background-color: transparent;
        color: rgba(94,93,82,1);
        text-align: left;
      }
    }

    td {
      text-align: right;

      @media (max-width: @bp-sm) {
      	// font-size:.8em;
      	   text-align:right;
      }

      @media (max-width: @bp-md) {
        text-align:right; 
         	font-size:.8em;
         	border-top:1px solid @brand-color-10;
      }

       @media (min-width: @bp-lg) {
        text-align:left; 

      }
    }

    td[data-type=currency] {
      text-align: right; 
    }

    td[data-title]:before {
      content: attr(data-title);
      float: left;
      font-size: .8em;
      color: rgba(94,93,82,.75);

      @media (min-width: @bp-sm) {
        font-size: .9em; 
      }

      @media (min-width: @bp-md) {
        // Don’t show data-title labels 
        content: none; 
      }
    } 
  }
}

